	public static String[] turn_into_5array(String input)
	{
		
		int length = input.length();
		String[] output = new String[length/5];
		int temp=0;
		for(int i=0; i<length-1; i+=5)
		{
			String temp2 = input.substring(i, i+5);
			output[temp]= temp2.toString();
			temp+=1;
		}		
		return output;
	}
	
